settings.custom_liquid:
{% form 'product', product %}
  <!-- Variant ID (required for adding to cart) -->
  <input type="hidden" name="id" value="{{ product.selected_or_first_available_variant.id }}">

  <!-- Add to Cart Button -->
  <button type="submit" class="add-to-cart-btn">
    Add to Cart
  </button>
{% endform %}

<script>
  document.querySelectorAll('[name="Color"], [name="Size"]').forEach((el) => {
    el.addEventListener('change', () => {
      const selectedColor = document.querySelector('input[name="Color"]:checked')?.value;
      const selectedSize = document.querySelector('input[name="Size"]:checked')?.value;

      const variants = {{ product.variants | json }};
      const matchingVariant = variants.find(v =>
        v.option1 === selectedColor && v.option2 === selectedSize
      );

      if (matchingVariant) {
        document.querySelector('input[name="id"]').value = matchingVariant.id;
      }
    });
  });
</script>
